UPDATE products
SET product_price = 50.00
WHERE product_id = 1;

UPDATE products
SET product_price = product_price + 10
WHERE product_id IN (SELECT product_id FROM costs WHERE shipping_cost > 20);

UPDATE costs
SET customs_cost = customs_cost * 1.1
WHERE product_id IN (SELECT product_id FROM products WHERE product_price > 100);
