UPDATE car_dealerships
SET sold_price = sold_price * 1.1
WHERE sold_date > '2022-01-01';

UPDATE sales_representatives
SET hire_date = hire_date - INTERVAL 1 YEAR
WHERE dealership_id IN (SELECT dealership_id FROM dealerships WHERE location = 'New York');

UPDATE sales_forecast
SET forecast_value = forecast_value * 1.05
WHERE forecast_date BETWEEN '2022-01-01' AND '2022-06-30';
