tables.table_schema:
CREATE TABLE ProductImprovements (
  improvement_id INT PRIMARY KEY,
  product_id INT,
  improvement_text TEXT,
  implemented BOOLEAN
);

CREATE TABLE FeedbackHistory (
  feedback_id INT PRIMARY KEY,
  review_id INT,
  improvement_id INT,
  feedback_date DATE
);

CREATE TABLE customers (
  id INT PRIMARY KEY,
  full_name VARCHAR(255),
  email VARCHAR(255),
  phone_number VARCHAR(15)
);

CREATE TABLE products (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  description TEXT,
  price DECIMAL(10,2)
);

CREATE TABLE ProductReviews (
    review_id INT PRIMARY KEY,
    product_id INT,
    customer_name VARCHAR(50),
    review_text TEXT
);

CREATE TABLE ProductRatings (
    rating_id INT PRIMARY KEY,
    product_id INT,
    rating FLOAT,
    review_id INT
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(50),
    phone_number VARCHAR(20)
);

CREATE TABLE Products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE Feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    feedback_date DATE
);
