tables.table_schema:
CREATE TABLE revenue (
  date DATE,
  amount DECIMAL
);

CREATE TABLE profit_margin (
  date DATE,
  percentage DECIMAL
);

CREATE TABLE sales_performance (
  date DATE,
  revenue DECIMAL,
  units_sold INT
);

CREATE TABLE revenue_reports (
    report_id INT PRIMARY KEY,
    date DATE,
    revenue DECIMAL,
    revenue_source VARCHAR
);

CREATE TABLE profit_margin_reports (
    report_id INT PRIMARY KEY,
    date DATE,
    profit_margin DECIMAL,
    product_name VARCHAR
);

CREATE TABLE sales_performance_reports (
    report_id INT PRIMARY KEY,
    date DATE,
    sales_amount DECIMAL,
    sales_rep VARCHAR
);

CREATE TABLE business_analysis_reports (
    report_id INT PRIMARY KEY,
    date DATE,
    analysis_type VARCHAR,
    analysis_result TEXT
);
