tables.table_schema:
CREATE TABLE ProductReturns(
    return_id INT PRIMARY KEY,
    product_id INT,
    return_date DATE,
    return_reason TEXT
);

CREATE TABLE ReturnAnalysis(
    analysis_id INT PRIMARY KEY,
    return_id INT,
    analysis_date DATE,
    analysis_result TEXT
);

CREATE TABLE return_analysis (
  analysis_id UUID PRIMARY KEY,
  return_id UUID,
  analysis_result TEXT,
  analysis_date TIMESTAMP
);

CREATE TABLE quality_improvement (
  improvement_id UUID PRIMARY KEY,
  analysis_id UUID,
  improvement_plan TEXT,
  implementation_date TIMESTAMP
);

CREATE TABLE quality_improvement (
    improvement_id SERIAL PRIMARY KEY,
    analysis_id INT,
    improvement_description TEXT,
    improvement_date DATE
);

CREATE TABLE product_returns (
    return_id UUID PRIMARY KEY,
    product_name VARCHAR(255),
    return_reason TEXT,
    return_date TIMESTAMP
);

CREATE TABLE return_customers (
    customer_id UUID PRIMARY KEY,
    customer_name VARCHAR(255),
    customer_email VARCHAR(255),
    customer_phone VARCHAR(15)
);

CREATE TABLE return_locations (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR(255),
    location_address TEXT,
    location_manager VARCHAR(255)
);

CREATE TABLE return_statistics (
    statistics_id UUID PRIMARY KEY,
    analysis_id UUID,
    total_returns INT,
    return_rate FLOAT
);
