tables.table_schema:
CREATE TABLE NetworkConfigurations ( 
    service_id SERIAL PRIMARY KEY, 
    service_name VARCHAR(50) NOT NULL, 
    network_type VARCHAR(50) NOT NULL, 
    configuration JSONB 
);

CREATE TABLE network_configurations (
    id INT PRIMARY KEY,
    service_name VARCHAR(255) NOT NULL,
    service_type VARCHAR(50) NOT NULL,
    configuration_json JSONB NOT NULL
);

CREATE TABLE service_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(255) NOT NULL,
    provider_location VARCHAR(100) NOT NULL
);

CREATE TABLE network_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50) NOT NULL,
    service_type VARCHAR(50) NOT NULL,
    service_description TEXT
);

CREATE TABLE network_configs (
    config_id INT PRIMARY KEY,
    service_id INT NOT NULL,
    config_name VARCHAR(50) NOT NULL,
    config_details JSONB
);

CREATE TABLE network_subscribers (
    subscriber_id INT PRIMARY KEY,
    service_id INT NOT NULL,
    subscriber_name VARCHAR(50) NOT NULL,
    subscriber_email VARCHAR(50)
);

CREATE TABLE NetworkProviders (
    id INT PRIMARY KEY,
    provider_name VARCHAR(100) NOT NULL,
    service_type VARCHAR(50) NOT NULL
);

CREATE TABLE NetworkDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(100) NOT NULL,
    provider_id INT REFERENCES NetworkProviders(id) NOT NULL,
    configuration JSONB
);

CREATE TABLE NetworkLocations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL
);

CREATE TABLE NetworkConnections (
    connection_id INT PRIMARY KEY,
    location_id INT REFERENCES NetworkLocations(location_id) NOT NULL,
    device_id INT REFERENCES NetworkDevices(device_id) NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE ServiceProviders ( 
    id INT PRIMARY KEY, 
    provider_name VARCHAR(50) 
);
