);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    date_registered DATE
);

CREATE TABLE data_plans (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50),
    data_limit INT,
    price DECIMAL(10, 2)
);

CREATE TABLE data_recommendations (
    recommendation_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    recommended_plan_id INT REFERENCES data_plans(plan_id),
    reason VARCHAR(255)
);

CREATE TABLE data_consumption_patterns (
    pattern_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    data_consumption_pattern JSONB
);
