tables.table_schema:
CREATE TABLE suspicious_transactions (
    transaction_id UUID PRIMARY KEY,
    amount DECIMAL,
    timestamp TIMESTAMP,
    flagged BOOLEAN
);

CREATE TABLE suspicious_patterns (
    pattern_id UUID PRIMARY KEY,
    pattern_description TEXT,
    pattern_criteria JSONB
);

CREATE TABLE suspicious_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR,
    pattern_description VARCHAR,
);

CREATE TABLE transaction_flags (
    flag_id INT PRIMARY KEY,
    transaction_id INT,
    flag_reason TEXT,
);

CREATE TABLE flag_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR,
    category_description TEXT,
);
