tables.table_schema:
CREATE TABLE spa_appointments (
  appointment_id INT PRIMARY KEY,
  customer_id INT,
  appointment_date DATE,
  time_slot TIME,
  therapist_id INT
);

CREATE TABLE customer_preferences (
  customer_id INT PRIMARY KEY,
  favorite_therapist_id INT,
  favorite_service VARCHAR(50),
  preferred_time TIME
);

CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    favorite_therapist VARCHAR(50),
    preferred_service VARCHAR(50),
    wellness_goal TEXT
);

CREATE TABLE spa_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    description TEXT,
    price DECIMAL(10, 2)
);
