tables.table_schema:
CREATE TABLE legislative_records (
    record_id SERIAL PRIMARY KEY,
    record_title VARCHAR(255) NOT NULL,
    record_content TEXT,
    record_date DATE
);

CREATE TABLE voting_data (
    vote_id SERIAL PRIMARY KEY,
    voter_name VARCHAR(255) NOT NULL,
    vote_decision VARCHAR(10) NOT NULL,
    vote_date TIMESTAMP
);

CREATE TABLE member_info (
    member_id SERIAL PRIMARY KEY,
    member_name VARCHAR(255) NOT NULL,
    member_position VARCHAR(50),
    member_party VARCHAR(50)
);

CREATE TABLE LegislativeRecords (
  record_id UUID PRIMARY KEY,
  record_title TEXT,
  record_type TEXT,
  record_date DATE
);

CREATE TABLE Legislators (
  legislator_id UUID PRIMARY KEY,
  legislator_name TEXT,
  legislator_party TEXT,
  date_of_birth DATE
);

CREATE TABLE Committees (
  committee_id UUID PRIMARY KEY,
  committee_name TEXT,
  committee_description TEXT
);
