tables.table_schema:
CREATE TABLE CrimeReports (
    report_id UUID PRIMARY KEY,
    crime_type VARCHAR(50),
    location VARCHAR(100),
    report_date DATE
);

CREATE TABLE crimes (
    crime_id INT PRIMARY KEY,
    crime_type VARCHAR(255),
    crime_location VARCHAR(255),
    crime_date DATE
);

CREATE TABLE suspects (
    suspect_id INT PRIMARY KEY,
    suspect_name VARCHAR(255),
    suspect_age INT,
    suspect_gender CHAR(1)
);

CREATE TABLE incidents (
    incident_id INT PRIMARY KEY,
    crime_id INT,
    suspect_id INT,
    incident_description TEXT
);

CREATE TABLE crime_locations (
  location_id INT PRIMARY KEY,
  city VARCHAR(50),
  state VARCHAR(50),
  country VARCHAR(50)
);

CREATE TABLE officers (
  officer_id INT PRIMARY KEY,
  officer_name VARCHAR(50),
  department VARCHAR(50),
  badge_number INT
);

CREATE TABLE crime_analysis (
  analysis_id INT PRIMARY KEY,
  report_id INT,
  analysis_result TEXT,
  analyst_id INT
);
