tables.table_schema:
CREATE TABLE property_prices (
  property_id INT PRIMARY KEY,
  price DECIMAL,
  date DATE
);

CREATE TABLE market_trends (
  trend_id INT PRIMARY KEY,
  trend_description TEXT,
  trend_date DATE
);

CREATE TABLE property_prices (
    price_id UUID PRIMARY KEY,
    property_id UUID REFERENCES market_trends(property_id),
    price DECIMAL,
    price_date DATE
);

CREATE TABLE property_history (
    history_id UUID PRIMARY KEY,
    property_id UUID REFERENCES market_trends(property_id),
    historic_price DECIMAL,
    transaction_date DATE
);

CREATE TABLE MarketTrends (
    trend_id INT PRIMARY KEY,
    property_id INT,
    price DECIMAL,
    trend_date DATE
);

CREATE TABLE MarketSegments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(255)
);

CREATE TABLE PropertyOwners (
    owner_id INT PRIMARY KEY,
    property_id INT,
    owner_name VARCHAR(255),
    contact_email VARCHAR(255)
);

CREATE TABLE MarketStatistics (
    stat_id INT PRIMARY KEY,
    trend_id INT,
    segment_id INT,
    average_price DECIMAL
);
