tables.table_schema:
CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    manufacturing_date DATE,
    quality_score INT
);

CREATE TABLE manufacturing_steps (
    step_id INT PRIMARY KEY,
    step_name VARCHAR(255) NOT NULL,
    description TEXT,
    duration_minutes INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    manufacturing_date DATE,
    quality_score INT
);

CREATE TABLE inspectors (
    inspector_id INT PRIMARY KEY,
    inspector_name VARCHAR(255)
);

CREATE TABLE defects (
    defect_id INT PRIMARY KEY,
    product_id INT,
    defect_type VARCHAR(50),
    defect_description TEXT
);

CREATE TABLE products (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    manufacturing_date DATE,
    quality_rating FLOAT
);

CREATE TABLE manufacturing_sites (
    site_id SERIAL PRIMARY KEY,
    site_location VARCHAR(255) NOT NULL,
    site_manager VARCHAR(100),
    capacity INTEGER
);

CREATE TABLE quality_tests (
    test_id SERIAL PRIMARY KEY,
    product_id UUID REFERENCES products(product_id),
    test_date DATE,
    test_result BOOLEAN
);

CREATE TABLE quality_engineers (
    engineer_id SERIAL PRIMARY KEY,
    engineer_name VARCHAR(100) NOT NULL,
    engineer_specialization VARCHAR(255),
    contact_info VARCHAR(255)
);
