tables.table_schema:
CREATE TABLE production_costs (
  plant_id INT,
  cost_amount DECIMAL,
  date DATE
);

CREATE TABLE performance_metrics (
  plant_id INT,
  yield DECIMAL,
  efficiency DECIMAL,
  date DATE
);

CREATE TABLE production_costs (
    id INT PRIMARY KEY,
    cost_type VARCHAR(50),
    cost_amount DECIMAL(10, 2)
);

CREATE TABLE yields (
    id INT PRIMARY KEY,
    product_id INT,
    yield_amount DECIMAL(10, 2),
    yield_date DATE
);

CREATE TABLE efficiency (
    id INT PRIMARY KEY,
    machine_id INT,
    efficiency_percentage DECIMAL(5, 2),
    efficiency_date DATE
);

CREATE TABLE performance_monitoring (
    id INT PRIMARY KEY,
    report_id INT,
    report_date DATE,
    report_description TEXT
);

CREATE TABLE production_costs (
    id INT PRIMARY KEY,
    production_date DATE,
    cost_amount DECIMAL
);

CREATE TABLE yield_data (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    yield_amount DECIMAL
);

CREATE TABLE efficiency_data (
    id INT PRIMARY KEY,
    production_date DATE,
    efficiency_score DECIMAL
);

CREATE TABLE employee_data (
    id INT PRIMARY KEY,
    employee_name VARCHAR(255),
    department VARCHAR(255),
    position VARCHAR(255)
);
