tables.table_schema:
CREATE TABLE employee_training_records (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50) NOT NULL,
    training_program VARCHAR(100) NOT NULL,
    completion_status BOOLEAN
);

CREATE TABLE training_logs (
    log_id SERIAL PRIMARY KEY,
    employee_id INT REFERENCES employee_training_records(employee_id),
    training_date DATE NOT NULL,
    training_duration INTERVAL,
    trainer_name VARCHAR(50)
);

CREATE TABLE employee_records (
    employee_id UUID PRIMARY KEY,
    training_title VARCHAR(255) NOT NULL,
    training_date DATE NOT NULL,
    training_duration_hours INT NOT NULL
);

CREATE TABLE training_categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE employee_training_records (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    training_type VARCHAR(50),
    training_date DATE
);

CREATE TABLE training_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50),
    duration_hours INT
);

CREATE TABLE training_instructors (
    instructor_id INT PRIMARY KEY,
    instructor_name VARCHAR(50),
    specialty VARCHAR(50)
);

CREATE TABLE employee_department (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);
