tables.table_schema:
CREATE TABLE Participants (participant_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  age INT,
  gender VARCHAR(10),
  condition VARCHAR(100)
);

CREATE TABLE Trials (trial_id INT PRIMARY KEY,
  trial_name VARCHAR(100) NOT NULL,
  start_date DATE,
  end_date DATE,
  researcher VARCHAR(50)
);

CREATE TABLE Observations (observation_id INT PRIMARY KEY,
  participant_id INT,
  trial_id INT,
  observation_date DATE,
  result VARCHAR(255)
);

CREATE TABLE Measurements (measurement_id INT PRIMARY KEY,
  observation_id INT,
  measurement_type VARCHAR(50),
  value FLOAT
);
