tables.table_schema:
CREATE TABLE research_topics (
  research_topic_id INT PRIMARY KEY,
  topic_name VARCHAR(100) NOT NULL
);

CREATE TABLE research_keywords (
  keyword_id INT PRIMARY KEY,
  keyword_name VARCHAR(50) NOT NULL,
  research_topic_id INT,
  FOREIGN KEY (research_topic_id) REFERENCES research_topics(research_topic_id)
);

CREATE TABLE research_topics ( 
    id SERIAL PRIMARY KEY, 
    topic_name VARCHAR(255) NOT NULL, 
    topic_category VARCHAR(100), 
    created_at TIMESTAMP NOT NULL 
);

CREATE TABLE research_keywords ( 
    id SERIAL PRIMARY KEY, 
    keyword_name VARCHAR(255) NOT NULL, 
    relevance_score INT, 
    created_at TIMESTAMP NOT NULL 
);

CREATE TABLE research_topic_keywords ( 
    id SERIAL PRIMARY KEY, 
    topic_id INT REFERENCES research_topics(id), 
    keyword_id INT REFERENCES research_keywords(id), 
    created_at TIMESTAMP NOT NULL 
);

CREATE TABLE research_topics (
  id UUID PRIMARY KEY,
  topic_name TEXT,
  researcher_name TEXT,
  research_area TEXT
);

CREATE TABLE research_keywords (
  id UUID PRIMARY KEY,
  keyword TEXT,
  relevance_score INT,
  research_topic_id UUID REFERENCES research_topics(id)
);

CREATE TABLE research_publications (
  id UUID PRIMARY KEY,
  publication_title TEXT,
  publication_date DATE,
  research_topic_id UUID REFERENCES research_topics(id)
);

CREATE TABLE research_authors (
  id UUID PRIMARY KEY,
  author_name TEXT,
  author_email TEXT,
  research_publication_id UUID REFERENCES research_publications(id)
);

CREATE TABLE Researcher (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR(255) NOT NULL,
    researcher_email VARCHAR(255) NOT NULL
);

CREATE TABLE ResearchTopic (
    topic_id UUID PRIMARY KEY,
    topic_name VARCHAR(255) NOT NULL
);

CREATE TABLE Keyword (
    keyword_id UUID PRIMARY KEY,
    keyword_name VARCHAR(255) NOT NULL
);

CREATE TABLE ResearcherResearchTopic (
    researcher_id UUID REFERENCES Researcher(researcher_id),
    topic_id UUID REFERENCES ResearchTopic(topic_id),
    PRIMARY KEY (researcher_id, topic_id)
);

CREATE TABLE ResearchTopicKeyword (
    topic_id UUID REFERENCES ResearchTopic(topic_id),
    keyword_id UUID REFERENCES Keyword(keyword_id),
    PRIMARY KEY (topic_id, keyword_id)
);
