tables.table_schema:
CREATE TABLE AttendanceData (
    student_id INT PRIMARY KEY,
    course_id INT,
    date DATE,
    status VARCHAR(10)
);

CREATE TABLE student_attendance (
    student_id INT PRIMARY KEY,
    course_id INT,
    date_attended DATE
);

CREATE TABLE course_requirements (
    course_id INT PRIMARY KEY,
    required_attendance INT,
    passing_grade DECIMAL
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    student_email VARCHAR(50) UNIQUE
);

CREATE TABLE courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    course_code VARCHAR(10) UNIQUE
);

CREATE TABLE attendance (
    attendance_id INT PRIMARY KEY,
    student_id INT REFERENCES students(student_id),
    course_id INT REFERENCES courses(course_id),
    attendance_date DATE
);

CREATE TABLE Students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(100) NOT NULL,
    enrollment_year INT
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(100) NOT NULL,
    course_code VARCHAR(10)
);

CREATE TABLE CourseRequirements (
    course_id INT,
    required_attendance_percentage INT
);

CREATE TABLE students (
    student_id UUID PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE courses (
    course_id UUID PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    course_code VARCHAR(20) UNIQUE NOT NULL
);

CREATE TABLE enrollments (
    enrollment_id UUID PRIMARY KEY,
    student_id UUID REFERENCES students(student_id),
    course_id UUID REFERENCES courses(course_id),
    enrollment_date DATE NOT NULL
);

CREATE TABLE attendance (
    attendance_id UUID PRIMARY KEY,
    enrollment_id UUID REFERENCES enrollments(enrollment_id),
    attendance_date DATE NOT NULL,
    is_present BOOLEAN NOT NULL
);

CREATE TABLE assignments (
    assignment_id UUID PRIMARY KEY,
    course_id UUID REFERENCES courses(course_id),
    assignment_name VARCHAR(50) NOT NULL,
    due_date DATE NOT NULL
);
