tables.table_schema:
CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(255),
  date_joined DATE
);

CREATE TABLE IF NOT EXISTS Shows (show_id INT PRIMARY KEY, show_title VARCHAR(255), show_description TEXT, show_genre VARCHAR(50));

CREATE TABLE IF NOT EXISTS Episodes (episode_id INT PRIMARY KEY, show_id INT, episode_title VARCHAR(255), episode_number INT, episode_description TEXT, FOREIGN KEY (show_id) REFERENCES Shows(show_id));

CREATE TABLE IF NOT EXISTS Users (user_id INT PRIMARY KEY, username VARCHAR(50), email VARCHAR(255), registration_date DATE);

CREATE TABLE IF NOT EXISTS ViewingHistory (view_id INT PRIMARY KEY, user_id INT, show_id INT, episode_id INT, viewed_date TIMESTAMP, FOREIGN KEY (user_id) REFERENCES Users(user_id), FOREIGN KEY (show_id) REFERENCES Shows(show_id), FOREIGN KEY (episode_id) REFERENCES Episodes(episode_id));

CREATE TABLE IF NOT EXISTS UserPreferences (user_id INT PRIMARY KEY, preferred_genre VARCHAR(50), preferred_language VARCHAR(50), FOREIGN KEY (user_id) REFERENCES Users(user_id));
