tables.table_schema:
CREATE TABLE fuel_consumption_report (
  vehicle_id INT,
  date DATE,
  fuel_consumed DECIMAL,
  cost DECIMAL
);

CREATE TABLE transportation_cost_report (
  route_id INT,
  date DATE,
  total_cost DECIMAL
);

CREATE TABLE fuel_consumption (
  vehicle_id INT PRIMARY KEY,
  fuel_type TEXT,
  consumption DECIMAL,
  cost DECIMAL
);

CREATE TABLE FuelType (id INT PRIMARY KEY, type VARCHAR(50) NOT NULL);
CREATE TABLE Vehicle (id INT PRIMARY KEY, model VARCHAR(50) NOT NULL, fuel_type_id INT NOT NULL, FOREIGN KEY (fuel_type_id) REFERENCES FuelType(id));
CREATE TABLE Driver (id INT PRIMARY KEY, name VARCHAR(50) NOT NULL, license_no INT UNIQUE NOT NULL);
CREATE TABLE Trip (id INT PRIMARY KEY, vehicle_id INT NOT NULL, driver_id INT NOT NULL, distance FLOAT NOT NULL, cost DECIMAL(10,2) NOT NULL, date DATE NOT NULL, FOREIGN KEY (vehicle_id) REFERENCES Vehicle(id), FOREIGN KEY (driver_id) REFERENCES Driver(id));
CREATE TABLE Report (id INT PRIMARY KEY, trip_id INT NOT NULL, fuel_cost DECIMAL(10,2) NOT NULL, report_date DATE NOT NULL, FOREIGN KEY (trip_id) REFERENCES Trip(id));
