tables.table_schema:
CREATE TABLE production (
    production_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    energy_source VARCHAR(255),
    energy_output DECIMAL
);

CREATE TABLE allocation (
    allocation_id INT PRIMARY KEY,
    production_id INT REFERENCES production(production_id),
    allocation_date DATE,
    allocated_amount DECIMAL
);

CREATE TABLE renewable_energy_production (
    id INT PRIMARY KEY,
    production_date DATE,
    production_amount DECIMAL
);

CREATE TABLE resource_allocation (
    id INT PRIMARY KEY,
    allocation_date DATE,
    allocated_resource DECIMAL,
    location VARCHAR(255)
);

CREATE TABLE energy_consumption (
    id INT PRIMARY KEY,
    consumption_date DATE,
    consumption_amount DECIMAL,
    device_id INT
);

CREATE TABLE EnergyProduction (
    id INT PRIMARY KEY,
    facility_name TEXT,
    production_capacity INT,
    production_date DATE
);

CREATE TABLE ResourcesAllocation (
    id INT PRIMARY KEY,
    facility_id INT,
    resource_type TEXT,
    allocation_quantity INT
);

CREATE TABLE Facilities (
    id INT PRIMARY KEY,
    facility_name TEXT,
    location TEXT
);

CREATE TABLE EnergyConsumption (
    id INT PRIMARY KEY,
    facility_id INT,
    consumption_amount INT,
    consumption_date DATE
);

CREATE TABLE MaintenanceLog (
    id INT PRIMARY KEY,
    facility_id INT,
    maintenance_type TEXT,
    maintenance_date DATE
);
