tables.table_schema:
CREATE TABLE EnergyDemandForecast (
  forecast_id INT PRIMARY KEY,
  date DATE,
  time TIME,
  energy_demand FLOAT
);

CREATE TABLE EnergyDemandForecast (
  id INT PRIMARY KEY,
  date DATE,
  time TIME,
  demand DECIMAL
);

CREATE TABLE ResourceNeeds (
  id INT PRIMARY KEY,
  resource_name VARCHAR(255),
  resource_type VARCHAR(50),
  quantity INT
);

CREATE TABLE energy_demand_forecast (
  id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  energy_demand FLOAT NOT NULL
);

CREATE TABLE resource_needs (
  id SERIAL PRIMARY KEY,
  resource_name VARCHAR(50) NOT NULL,
  resource_type VARCHAR(20) NOT NULL
);

CREATE TABLE resource_availability (
  id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  resource_qty INT NOT NULL
);

CREATE TABLE EnergyConsumptionForecast (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    forecast_date DATE,
    forecasted_demand DECIMAL
);

CREATE TABLE WeatherData (
    id INT PRIMARY KEY,
    city VARCHAR(255),
    temperature DECIMAL,
    humidity DECIMAL
);

CREATE TABLE ResourceAvailability (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    resource_type VARCHAR(255),
    available_quantity DECIMAL
);

CREATE TABLE EnergyDemandProjection (
    id INT PRIMARY KEY,
    forecast_date DATE,
    projected_demand DECIMAL,
    resource_need VARCHAR(255)
);

CREATE TABLE EnergyConsumption(
   timestamp TIMESTAMPTZ,
   location TEXT,
   demand FLOAT
);

CREATE TABLE WeatherForecast(
   timestamp TIMESTAMPTZ,
   location TEXT,
   temperature FLOAT,
   humidity FLOAT
);

CREATE TABLE EnergyProduction(
   timestamp TIMESTAMPTZ,
   location TEXT,
   production FLOAT
);

CREATE TABLE DemandForecast(
   timestamp TIMESTAMPTZ,
   location TEXT,
   demand_forecast FLOAT
);

CREATE TABLE ResourceAllocation(
   timestamp TIMESTAMPTZ,
   location TEXT,
   resource_alloc FLOAT
);
