tables.table_schema:
CREATE TABLE power_grid_performance (
    id INT PRIMARY KEY,
    location VARCHAR,
    power_consumption DECIMAL,
    voltage DECIMAL
);

CREATE TABLE outages_prevention (
    id INT PRIMARY KEY,
    grid_id INT,
    prevention_method VARCHAR,
    success_rate FLOAT
);

CREATE TABLE power_grid_locations (
    location_id INT PRIMARY KEY,
    location_name TEXT,
    city TEXT,
    state TEXT
);

CREATE TABLE power_grid_devices (
    device_id INT PRIMARY KEY,
    device_name TEXT,
    location_id INT REFERENCES power_grid_locations(location_id),
    device_type TEXT
);

CREATE TABLE power_grid_readings (
    reading_id INT PRIMARY KEY,
    device_id INT REFERENCES power_grid_devices(device_id),
    reading_value FLOAT,
    timestamp TIMESTAMP
);
