tables.table_schema:
CREATE TABLE energy_distribution (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    energy_type VARCHAR(50),
    distribution_time TIMESTAMP
);

CREATE TABLE cost_reduction (
    id INT PRIMARY KEY,
    strategy VARCHAR(50),
    cost_reduction DECIMAL(10,2),
    implementation_date DATE
);

CREATE TABLE EnergyProviders (
   provider_id INT PRIMARY KEY,
   provider_name VARCHAR(255),
   provider_location VARCHAR(255)
);

CREATE TABLE EnergyConsumers (
   consumer_id INT PRIMARY KEY,
   consumer_name VARCHAR(255),
   consumption_rate DECIMAL
);

CREATE TABLE EnergyCosts (
   cost_id INT PRIMARY KEY,
   provider_id INT,
   consumer_id INT,
   cost_per_kWh DECIMAL
);
