tables.table_schema:
CREATE TABLE infrastructure_list (
    location_id INT PRIMARY KEY,
    infrastructure_name TEXT,
    installation_date DATE,
    last_maintenance_date DATE
);

CREATE TABLE Infrastructure (
    infrastructure_id UUID PRIMARY KEY,
    infrastructure_type VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE Technician (
    technician_id UUID PRIMARY KEY,
    technician_name VARCHAR(50) NOT NULL,
    technician_role VARCHAR(50) NOT NULL
);

CREATE TABLE MaintenanceTask (
    task_id UUID PRIMARY KEY,
    schedule_id UUID NOT NULL,
    task_description VARCHAR(200) NOT NULL,
    task_status VARCHAR(50) NOT NULL
);

CREATE TABLE TaskAssignment (
    assignment_id UUID PRIMARY KEY,
    task_id UUID NOT NULL,
    technician_id UUID NOT NULL,
    assignment_date DATE NOT NULL
);
