tables.table_schema:
CREATE TABLE GreenhouseGasEmissions (
    id SERIAL PRIMARY KEY,
    gas_type VARCHAR(50) NOT NULL,
    emission_amount DECIMAL NOT NULL,
    reporting_year INT NOT NULL
);

CREATE TABLE EmissionSources (
    id SERIAL PRIMARY KEY,
    source_name VARCHAR(100) NOT NULL,
    location VARCHAR(100),
    emission_category VARCHAR(50)
);

CREATE TABLE ReportingEntities (
    id SERIAL PRIMARY KEY,
    entity_name VARCHAR(100) NOT NULL,
    sector VARCHAR(50),
    country VARCHAR(50) NOT NULL
);

CREATE TABLE emissions_reports (
   report_id INT PRIMARY KEY,
   location_id INT,
   greenhouse_gas_type VARCHAR(50),
   emission_amount DECIMAL
);

CREATE TABLE locations (
   location_id INT PRIMARY KEY,
   location_name VARCHAR(100),
   latitude DECIMAL,
   longitude DECIMAL
);

CREATE TABLE reports_history (
   history_id INT PRIMARY KEY,
   report_id INT,
   reporting_date DATE,
   submitted_by VARCHAR(100)
);

CREATE TABLE emissions_data (
    date TIMESTAMP,
    greenhouse_gas_type VARCHAR,
    amount FLOAT
);

CREATE TABLE monitoring_locations (
    location_id INT,
    location_name VARCHAR,
    country VARCHAR
);

CREATE TABLE emission_sources (
    source_id INT,
    source_name VARCHAR,
    location_id INT
);

CREATE TABLE emission_categories (
    category_id INT,
    category_name VARCHAR
);
