tables.table_schema:
CREATE TABLE FieldNutrientAnalysis (
    field_id INT PRIMARY KEY,
    nitrate_content FLOAT,
    phosphate_content FLOAT,
    potassium_content FLOAT
);

CREATE TABLE fields (
 field_id INT PRIMARY KEY,
 field_name VARCHAR(50) NOT NULL,
 soil_type VARCHAR(50) NOT NULL
);

CREATE TABLE Fields (
    field_id INT,
    field_name VARCHAR,
    field_location VARCHAR,
    PRIMARY KEY (field_id)
);

CREATE TABLE Nutrients (
    nutrient_id INT,
    nutrient_name VARCHAR,
    PRIMARY KEY (nutrient_id)
);
