tables.table_schema:
CREATE TABLE weather_data (
    id INT PRIMARY KEY,
    city_name TEXT,
    temperature FLOAT,
    humidity FLOAT,
    date_time TIMESTAMP
);

CREATE TABLE planting_times (
    id INT PRIMARY KEY,
    city_name TEXT,
    crop_name TEXT,
    optimal_planting_date DATE
);

CREATE TABLE weather_data ( 
    id INT PRIMARY KEY, 
    temperature FLOAT, 
    humidity FLOAT, 
    date TIMESTAMP 
);

CREATE TABLE rainfall_data ( 
    id INT PRIMARY KEY, 
    date TIMESTAMP, 
    amount FLOAT 
);

CREATE TABLE soil_quality ( 
    id INT PRIMARY KEY, 
    location VARCHAR(50), 
    ph_level FLOAT, 
    nutrient_level FLOAT 
);

CREATE TABLE harvest_data ( 
    id INT PRIMARY KEY, 
    plant_name VARCHAR(50), 
    harvest_date TIMESTAMP, 
    quantity INT 
);
