tables.table_schema:
CREATE TABLE Pesticide (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  usage_rate FLOAT,
  application_date DATE
);

CREATE TABLE Fertilizer (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  nutrient_content VARCHAR(100),
  application_date DATE
);

CREATE TABLE pesticide_usage (
    id INT PRIMARY KEY,
    pesticide_name VARCHAR(50),
    quantity DECIMAL(10,2),
    usage_date DATE
);

CREATE TABLE fertilizer_usage (
    id INT PRIMARY KEY,
    fertilizer_name VARCHAR(50),
    quantity DECIMAL(10,2),
    usage_date DATE
);
