tables.table_schema:
CREATE TABLE Crop (
    crop_id UUID PRIMARY KEY,
    crop_name STRING,
    crop_type STRING,
    irrigated_area DOUBLE PRECISION
);

CREATE TABLE Crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    irrigation_schedule TEXT,
    water_usage DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Farm (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    total_land_area DECIMAL(10, 2) NOT NULL
);

CREATE TABLE CropDetails (
   crop_id INT PRIMARY KEY,
   crop_name VARCHAR(255) NOT NULL,
   irrigation_requirements FLOAT
);

CREATE TABLE crop_data (
    crop_id SERIAL PRIMARY KEY,
    crop_name VARCHAR(50),
    crop_type VARCHAR(50),
    water_usage FLOAT
);

CREATE TABLE crop_location (
    location_id SERIAL PRIMARY KEY,
    crop_id INT,
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE water_source (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR(50),
    source_type VARCHAR(50)
);
