tables.table_schema:
CREATE TABLE policy_limits (
    id SERIAL PRIMARY KEY,
    policy_id INT NOT NULL,
    coverage_limit INT NOT NULL,
    deductible_amount INT NOT NULL
);

CREATE TABLE policy_coverage_limits (
    policy_id INT,
    coverage_limit DECIMAL,
    deductible DECIMAL
);

CREATE TABLE compliance_regulations (
    regulation_id INT,
    regulation_description TEXT,
    compliance_status BOOLEAN
);

CREATE TABLE PolicyLimits (
	policy_id INT PRIMARY KEY,
	coverage_limit INT NOT NULL,
	deductible INT NOT NULL
);

CREATE TABLE Regulations (
	regulation_id INT PRIMARY KEY,
	regulation_name VARCHAR(50) NOT NULL,
	compliance_date DATE NOT NULL
);

CREATE TABLE PolicyRegulations (
	policy_id INT,
	regulation_id INT,
	FOREIGN KEY (policy_id) REFERENCES PolicyLimits(policy_id),
	FOREIGN KEY (regulation_id) REFERENCES Regulations(regulation_id)
);

CREATE TABLE policy_limits (
id INT PRIMARY KEY,
customer_id INT,
coverage_limit DECIMAL,
deductible DECIMAL
);

CREATE TABLE regulations (
regulation_id INT PRIMARY KEY,
regulation_name TEXT,
requirement TEXT,
penalty DECIMAL
);

CREATE TABLE coverage_types (
cov_type_id INT PRIMARY KEY,
cov_type_name TEXT
);

CREATE TABLE policy_coverage (
policy_id INT PRIMARY KEY,
cov_type_id INT,
id INT
);

CREATE TABLE policy_coverage_limits (
    policy_id INT PRIMARY KEY,
    coverage_limit DECIMAL,
    deductible DECIMAL
);

CREATE TABLE regulatory_compliance (
    regulatory_id INT PRIMARY KEY,
    regulation_name VARCHAR(50),
    compliance_status BOOLEAN
);

CREATE TABLE policy_regulations (
    policy_id INT,
    regulatory_id INT,
    FOREIGN KEY (policy_id) REFERENCES policy_coverage_limits (policy_id),
    FOREIGN KEY (regulatory_id) REFERENCES regulatory_compliance (regulatory_id)
);

CREATE TABLE claims (
    claim_id INT PRIMARY KEY,
    policy_id INT,
    claim_amount DECIMAL,
    claim_date DATE,
    FOREIGN KEY (policy_id) REFERENCES policy_coverage_limits (policy_id)
);

CREATE TABLE policy_holders (
    holder_id INT PRIMARY KEY,
    holder_name VARCHAR(50),
    holder_address VARCHAR(100)
);
