tables.table_schema:
CREATE TABLE policyholders ( 
  policyholder_id UUID PRIMARY KEY, 
  full_name VARCHAR(100), 
  email VARCHAR(100), 
  phone_number VARCHAR(20) 
);

CREATE TABLE communications (
    communication_id INT PRIMARY KEY,
    policyholder_id INT,
    communication_date TIMESTAMP,
    communication_content TEXT
);

CREATE TABLE policyholders (
    policyholder_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE policies (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    policy_type VARCHAR(50),
    policyholder_id INT,
    policy_start_date DATE,
    policy_end_date DATE
);

CREATE TABLE communications_tracking (
    log_id INT PRIMARY KEY,
    communication_id INT,
    policyholder_id INT,
    communication_date TIMESTAMP
);

CREATE TABLE communication_attachments (
    id INT PRIMARY KEY,
    communication_id INT,
    attachment_type VARCHAR(50),
    attachment_data BYTEA
);
