tables.table_schema:
CREATE TABLE Equipment (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(255),
  serial_number VARCHAR(50),
  maintenance_interval INT
);

CREATE TABLE equipment (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    type VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE maintenance_schedule (
    id INT PRIMARY KEY,
    equipment_id INT,
    scheduled_date DATE,
    notes VARCHAR(255)
);

CREATE TABLE service_history (
    id INT PRIMARY KEY,
    equipment_id INT,
    service_date DATE,
    description TEXT
);

CREATE TABLE service_personnel (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    role VARCHAR(50)
);

CREATE TABLE Technicians (
	technician_id UUID PRIMARY KEY,
	technician_name VARCHAR(50),
	technician_email VARCHAR(50),
	technician_phone VARCHAR(15)
);
