CREATE TABLE utility_consumption (
  id INT PRIMARY KEY,
  utility_type TEXT,
  consumption_amount DECIMAL
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  name TEXT,
  address TEXT,
  email TEXT
);

CREATE TABLE electricity_usage_data (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ,
  kilowatt_hours FLOAT
);

CREATE TABLE water_usage_data (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ,
  gallons_used FLOAT
);

CREATE TABLE gas_usage_data (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ,
  cubic_feet_used FLOAT
);

CREATE TABLE electricity_usage (
    id INT PRIMARY KEY,
