tables.table_schema:
CREATE TABLE utility_usage (
    id INT PRIMARY KEY,
    customer_id INT,
    usage_date DATE,
    usage_amount DECIMAL
);

CREATE TABLE customer_details (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    location TEXT,
    contact_number TEXT
);

CREATE TABLE resource_types (
    resource_id INT PRIMARY KEY,
    resource_name TEXT
);

CREATE TABLE ElectricityUsage (
    id INT PRIMARY KEY,
    customer_id INT,
    usage_date DATE,
    usage_amount DECIMAL
);

CREATE TABLE WaterUsage (
    id INT PRIMARY KEY,
    customer_id INT,
    usage_date DATE,
    usage_amount DECIMAL
);

CREATE TABLE GasUsage (
    id INT PRIMARY KEY,
    customer_id INT,
    usage_date DATE,
    usage_amount DECIMAL
);

CREATE TABLE UtilityCustomers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    address VARCHAR(100),
    utility_type VARCHAR(20)
);

CREATE TABLE MeterReadings (
    id INT PRIMARY KEY,
    meter_id INT,
    reading_date DATE,
    reading_value DECIMAL
);
