tables.table_schema:
CREATE TABLE utilities_regulatory_compliance (
  id INT PRIMARY KEY,
  utility_name VARCHAR(50),
  regulator_name VARCHAR(50),
  compliance_status BOOLEAN
);

CREATE TABLE utilities_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    description TEXT,
    enforcement_date DATE
);

CREATE TABLE utilities (
    id SERIAL PRIMARY KEY,
    utility_name VARCHAR(50) NOT NULL,
    regulatory_compliance_level VARCHAR(20) NOT NULL,
    date_of_audit DATE
);

CREATE TABLE compliance_issues (
    issue_id SERIAL PRIMARY KEY,
    utility_id INT REFERENCES utilities(id),
    issue_description TEXT,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE audits (
    audit_id SERIAL PRIMARY KEY,
    utility_id INT REFERENCES utilities(id),
    audit_date DATE,
    auditor_name VARCHAR(50)
);

CREATE TABLE utilities (
  utility_id UUID PRIMARY KEY,
  utility_name VARCHAR(100) NOT NULL,
  region VARCHAR(50),
  regulatory_body VARCHAR(100) NOT NULL
);

CREATE TABLE regulatory_agencies (
  agency_id UUID PRIMARY KEY,
  agency_name VARCHAR(100) NOT NULL,
  jurisdiction VARCHAR(50) NOT NULL
);
