tables.table_schema:
CREATE TABLE infrastructure_data (
    id INT PRIMARY KEY,
    name STRING,
    location POINT,
    utility_type STRING
);

CREATE TABLE maintenance_logs (
    log_id INT PRIMARY KEY,
    infrastructure_id INT,
    maintenance_date DATE,
    maintenance_description STRING
);

CREATE TABLE utility_companies (
    company_id INT PRIMARY KEY,
    company_name TEXT,
    industry_sector TEXT
);

CREATE TABLE maintenance_records (
    record_id INT PRIMARY KEY,
    asset_id INT REFERENCES utility_assets(asset_id),
    maintenance_date DATE,
    maintenance_type TEXT
);

CREATE TABLE utility_jobs (
    job_id INT PRIMARY KEY,
    job_description TEXT,
    job_status TEXT,
    asset_id INT REFERENCES utility_assets(asset_id)
);

CREATE TABLE power_lines (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    length INT,
    voltage INT
);

CREATE TABLE transformers (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    installed_date DATE,
    capacity INT
);

CREATE TABLE substations (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(100),
    capacity INT
);

CREATE TABLE pole_locations (
    id INT PRIMARY KEY,
    latitude DECIMAL(10,8),
    longitude DECIMAL(11,8),
    status VARCHAR(20)
);

CREATE TABLE maintenance_logs (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    description TEXT,
    completed BOOLEAN
);
