tables.table_schema:
CREATE TABLE ProjectParticipants (
  participant_id UUID PRIMARY KEY,
  project_id UUID REFERENCES EnergyProjects(project_id),
  participant_name VARCHAR(100),
  role VARCHAR(50)
);

CREATE TABLE initiatives (
    initiative_id INT PRIMARY KEY,
    initiative_name VARCHAR,
    start_date DATE,
    target_savings DECIMAL
);

CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR,
    start_date DATE,
    end_date DATE
);

CREATE TABLE tasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR,
    due_date DATE,
    completed BOOLEAN
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR,
    email VARCHAR
);
