tables.table_schema:
CREATE TABLE DrugEfficacy (
  drug_id INT,
  drug_name VARCHAR,
  efficacy_score FLOAT
);

CREATE TABLE DrugSafety (
  drug_id INT,
  drug_name VARCHAR,
  safety_score FLOAT
);

CREATE TABLE DrugData (
  id INT PRIMARY KEY,
  drug_name VARCHAR(255),
  efficacy_score INT,
  safety_score INT
);

CREATE TABLE drug_records (
    drug_id bigint PRIMARY KEY,
    patient_id bigint,
    efficacy_score float,
    safety_score float
);

CREATE TABLE adverse_reactions (
    reaction_id bigint PRIMARY KEY,
    report_id bigint,
    reaction_type varchar(50),
    severity varchar(20)
);

CREATE TABLE efficacy_results (
    result_id bigint PRIMARY KEY,
    report_id bigint,
    efficacy_score float
);
