tables.table_schema:
CREATE TABLE Prescriptions (
  prescription_id INT,
  patient_id INT,
  prescription_name VARCHAR(255),
  refill_reminder BOOLEAN,
);

CREATE TABLE Reminders (
  reminder_id INT,
  patient_id INT,
  reminder_text TEXT,
  reminder_date TIMESTAMP,
);

CREATE TABLE RefillReminder (
  reminder_id UUID PRIMARY KEY,
  patient_id UUID NOT NULL,
  reminder_date DATE NOT NULL,
  reminder_message TEXT
);

CREATE TABLE ReminderTrigger (
  trigger_id UUID PRIMARY KEY,
  patient_id UUID NOT NULL,
  trigger_type VARCHAR(50) NOT NULL
);

CREATE TABLE refill_reminders (
  reminder_id INT PRIMARY KEY,
  prescription_id INT,
  reminder_date DATE,
  message VARCHAR(255)
);

CREATE TABLE Doctors (
  doctor_id INT PRIMARY KEY,
  doctor_name VARCHAR(50),
  doctor_specialty VARCHAR(50)
);

CREATE TABLE RefillReminders (
  reminder_id INT PRIMARY KEY,
  patient_id INT,
  reminder_date DATE,
  reminder_description TEXT
);

CREATE TABLE PrescriptionHistory (
  history_id INT PRIMARY KEY,
  prescription_id INT,
  refill_date DATE,
  refill_completed BOOLEAN
);
