tables.table_schema:
CREATE TABLE AdverseDrugReactions (
    report_id INT PRIMARY KEY,
    drug_name STRING,
    patient_id INT,
    reaction TEXT
);

CREATE TABLE Patients (
    patient_id INT PRIMARY KEY,
    patient_name STRING,
    age INT,
    gender CHAR(1)
);

CREATE TABLE Drugs (
    drug_id INT PRIMARY KEY,
    drug_name STRING,
    manufacturer STRING,
    expiration_date DATE
);

CREATE TABLE AdverseReaction (
  id INT PRIMARY KEY,
  patient_id INT,
  drug_name VARCHAR,
  reaction_type VARCHAR
);

CREATE TABLE Patient (
  id INT PRIMARY KEY,
  name VARCHAR,
  age INT,
  gender VARCHAR
);

CREATE TABLE Drug (
  id INT PRIMARY KEY,
  name VARCHAR,
  manufacturer VARCHAR,
  dosage VARCHAR
);

CREATE TABLE Reporting (
  id INT PRIMARY KEY,
  report_id INT,
  reported_date DATE,
  reporter_name VARCHAR
);
