tables.table_schema:
CREATE TABLE court_decisions (
   decision_id INT PRIMARY KEY,
   case_id INT,
   judge_name VARCHAR(50),
   decision_date DATE
);

CREATE TABLE legal_precedents (
   precedent_id INT PRIMARY KEY,
   precedent_text TEXT
);

CREATE TABLE cases (
   case_id INT PRIMARY KEY,
   case_number VARCHAR(20),
   court_name VARCHAR(50),
   filing_date DATE
);

CREATE TABLE attorneys (
   attorney_id INT PRIMARY KEY,
   attorney_name VARCHAR(50)
);

CREATE TABLE clients (
   client_id INT PRIMARY KEY,
   client_name VARCHAR(50),
   contact_info TEXT
);
