tables.table_schema:
CREATE DATABASE IF NOT EXISTS statutes;

CREATE TABLE statutes.regulations (
  id INT PRIMARY KEY,
  title VARCHAR(255),
  description TEXT,
  effective_date TIMESTAMP
);

CREATE TABLE legal_standing (
 statute_id SERIAL PRIMARY KEY,
 statute_name VARCHAR(255) NOT NULL,
 statute_description TEXT
);

CREATE TABLE compliance_requirements (
 requirement_id SERIAL PRIMARY KEY,
 requirement_name VARCHAR(255) NOT NULL,
 requirement_description TEXT
);

CREATE TABLE violation_records (
 record_id SERIAL PRIMARY KEY,
 statute_id INT REFERENCES legal_standing(statute_id),
 violation_date DATE,
 violation_description TEXT
);

CREATE TABLE compliance_reports (
 report_id SERIAL PRIMARY KEY,
 compliance_date DATE,
 compliance_status VARCHAR(50) CHECK (compliance_status IN ('Compliant', 'Non-compliant')),
 notes TEXT
);

CREATE TABLE audit_logs (
 log_id SERIAL PRIMARY KEY,
 action_performed VARCHAR(50) NOT NULL,
 action_timestamp TIMESTAMP,
 performed_by VARCHAR(100) NOT NULL
);
