tables.table_schema:
CREATE TABLE player_performance_metrics (
    player_id INT,
    goals_scored INT,
    assists INT,
    fouls INT
);

CREATE TABLE player_details (
    player_id INT,
    player_name VARCHAR,
    team_name VARCHAR,
    position VARCHAR
);

CREATE TABLE player_stats (
  player_id INT PRIMARY KEY,
  goals_scored INT,
  assists INT,
  fouls INT
);

CREATE TABLE team_stats (
  team_id INT PRIMARY KEY,
  goals_conceded INT,
  assists_given INT,
  fouls_committed INT
);

CREATE TABLE match_schedule (
  match_id INT PRIMARY KEY,
  home_team_id INT,
  away_team_id INT,
  match_date DATE
);

CREATE TABLE match_results (
  match_id INT PRIMARY KEY,
  home_team_score INT,
  away_team_score INT,
  match_winner VARCHAR(255)
);
