tables.table_schema:
CREATE TABLE Fans (
  fan_id INT PRIMARY KEY,
  fan_name VARCHAR(255) NOT NULL,
  fan_email VARCHAR(255) UNIQUE,
  fan_age INT
);

CREATE TABLE Campaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(255) NOT NULL,
  campaign_start_date DATE,
  campaign_end_date DATE
);

CREATE TABLE fans (
    fan_id INT PRIMARY KEY,
    fan_name VARCHAR(50),
    email VARCHAR(100),
    age INT
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(50),
    start_date DATE,
    end_date DATE
);
