tables.table_schema:
CREATE TABLE threats (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(50),
    threat_description TEXT,
    threat_level INT
);

CREATE TABLE mitigation_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    strategy_description TEXT,
    threat_id INT REFERENCES threats(threat_id)
);

CREATE TABLE threat_data (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(100),
    threat_description TEXT,
    threat_level INT
);

CREATE TABLE threats (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(50) NOT NULL,
    threat_description TEXT
);

CREATE TABLE threat_targets (
    target_id INT PRIMARY KEY,
    target_name VARCHAR(50) NOT NULL,
    target_location VARCHAR(100)
);

CREATE TABLE security_actions (
    action_id INT PRIMARY KEY,
    action_type VARCHAR(50) NOT NULL,
    action_description TEXT,
    event_id INT
);

CREATE TABLE threats (
  threat_id INT PRIMARY KEY,
  threat_name VARCHAR(255) NOT NULL,
  threat_level INT,
  threat_description TEXT
);

CREATE TABLE threat_actors (
  actor_id INT PRIMARY KEY,
  actor_name VARCHAR(255) NOT NULL,
  actor_type VARCHAR(50),
  actor_description TEXT
);

CREATE TABLE attack_methods (
  method_id INT PRIMARY KEY,
  method_name VARCHAR(255) NOT NULL,
  method_description TEXT
);
