CREATE TABLE media_content (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    genre VARCHAR(50) NOT NULL,
    release_date DATE NOT NULL,
    duration INTERVAL NOT NULL
);

CREATE TABLE media_directors (
    director_id SERIAL PRIMARY KEY,
    director_name VARCHAR(100) NOT NULL,
    media_id INT REFERENCES media_content(id)
);

CREATE TABLE media_actors (
    actor_id SERIAL PRIMARY KEY,
    actor_name VARCHAR(100) NOT NULL,
    media_id INT REFERENCES media_content(id)
);

CREATE TABLE media_ratings (
    rating_id SERIAL PRIMARY KEY,
    rating FLOAT NOT NULL,
    media_id INT REFERENCES media_content(id)
);

CREATE TABLE media_content (
    id UUID PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    genre VARCHAR(50) NOT NULL,
    release_date DATE NOT NULL,
    duration INT NOT NULL
);

CREATE TABLE media_genre (
    id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL
);

CREATE TABLE media_actor (
    id UUID PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    birth_date DATE,
    nationality VARCHAR(50)
);

CREATE TABLE media_director (
    id UUID PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    birth_date DATE,
    nationality VARCHAR(50)
);

CREATE TABLE media_review (
    id UUID PRIMARY KEY,
    media_id UUID REFERENCES media_content(id),
    rating FLOAT NOT NULL,
    review_text TEXT,
    reviewer_name VARCHAR(100)
);
