tables.table_schema:
CREATE TABLE viewer_behavior (
  viewer_id INT,
  content_id INT,
  timestamp TIMESTAMP,
  action_type VARCHAR
);

CREATE TABLE content_recommendations (
  recommendation_id INT,
  content_id INT,
  recommended_by VARCHAR,
  recommendation_timestamp TIMESTAMP
);

CREATE TABLE user_experience (
  viewer_id INT,
  interaction_id INT,
  rating INT,
  comments TEXT
);

CREATE TABLE viewers (
    viewer_id INT PRIMARY KEY,
    viewer_name VARCHAR(255),
    email VARCHAR(255),
    join_date TIMESTAMP
);

CREATE TABLE views (
    view_id INT PRIMARY KEY,
    viewer_id INT,
    video_id INT,
    view_date TIMESTAMP
);

CREATE TABLE recommendations (
    recommendation_id INT PRIMARY KEY,
    viewer_id INT,
    video_id INT,
    recommendation_date TIMESTAMP
);
