tables.table_schema:
CREATE TABLE rental_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    availability BOOLEAN
);

CREATE TABLE car_rental_rates (
    car_id INT PRIMARY KEY,
    location_id INT,
    rental_rate DECIMAL(10, 2),
    FOREIGN KEY (location_id) REFERENCES rental_locations(location_id)
);

CREATE TABLE rental_locations (
    location_id INT PRIMARY KEY,
    location_name TEXT
);

CREATE TABLE rental_cars (
    car_id INT PRIMARY KEY,
    location_id INT,
    car_model TEXT,
    rental_rate DECIMAL
);

CREATE TABLE rental_availability (
    availability_id INT PRIMARY KEY,
    car_id INT,
    available_date DATE,
    available_quantity INT
);

CREATE TABLE RentalLocations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50) NOT NULL
);

CREATE TABLE Cars (
    car_id INT PRIMARY KEY,
    car_name VARCHAR(50) NOT NULL,
    location_id INT,
    FOREIGN KEY (location_id) REFERENCES RentalLocations(location_id)
);

CREATE TABLE RentalRates (
    rate_id INT PRIMARY KEY,
    car_id INT,
    rate DECIMAL(8, 2) NOT NULL,
    FOREIGN KEY (car_id) REFERENCES Cars(car_id)
);

CREATE TABLE AvailableCars (
    availability_id INT PRIMARY KEY,
    car_id INT,
    available BOOLEAN NOT NULL,
    FOREIGN KEY (car_id) REFERENCES Cars(car_id)
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL
);
