tables.table_schema:
CREATE TABLE hotel_feedback (
    feedback_id INT PRIMARY KEY,
    hotel_name VARCHAR(50),
    feedback_text TEXT,
    star_rating INT
);

CREATE TABLE airline_feedback (
    feedback_id INT PRIMARY KEY,
    airline_name VARCHAR(50),
    feedback_text TEXT,
    star_rating INT
);

CREATE TABLE travel_service_feedback (
    feedback_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    feedback_text TEXT,
    star_rating INT
);

CREATE TABLE HotelFeedback (
    feedback_id INT PRIMARY KEY,
    hotel_id INT,
    rating FLOAT,
    feedback_text TEXT
);

CREATE TABLE AirlineFeedback (
    feedback_id INT PRIMARY KEY,
    airline_id INT,
    rating FLOAT,
    feedback_text TEXT
);

CREATE TABLE TravelServiceFeedback (
    feedback_id INT PRIMARY KEY,
    service_id INT,
    rating FLOAT,
    feedback_text TEXT
);

CREATE TABLE Customer (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    email TEXT
);

CREATE TABLE FeedbackResponse (
    response_id INT PRIMARY KEY,
    feedback_id INT,
    response_text TEXT
);
